import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Card, CardContent, Typography, TextField, Button, Box, Autocomplete } from "@mui/material";
import { LocalizationProvider, DesktopDatePicker } from "@mui/x-date-pickers";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import Cookies from "js-cookie";

const InitialForm = {
  amount: "",
  description: "",
  date: new Date(),
  category_id: "",
};

export default function TransactionForm({ fetchTransactions, editTransaction, setEditTransaction }) {
  const user = useSelector((state) => state.auth.user);
  const categories = user?.categories || [];
  const token = Cookies.get("token");
  const [form, setForm] = useState(InitialForm);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    if (editTransaction.amount !== undefined) {
      setForm(editTransaction);
      setEditMode(true);
    } else {
      setForm(InitialForm);
      setEditMode(false);
    }
  }, [editTransaction]);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleDate(newValue) {
    setForm({ ...form, date: newValue });
  }

  function handleSubmit(e) {
    e.preventDefault();
    editMode ? update() : create();
  }

  function handleCancel() {
    setForm(InitialForm);
    setEditMode(false);
    setEditTransaction({});
  }

  async function create() {
    const res = await fetch(`${process.env.REACT_APP_API_URL}/transaction`, {
      method: "POST",
      body: JSON.stringify(form),
      headers: {
        "content-type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    });
    if (res.ok) reload();
  }

  async function update() {
    const res = await fetch(`${process.env.REACT_APP_API_URL}/transaction/${editTransaction._id}`, {
      method: "PATCH",
      body: JSON.stringify(form),
      headers: {
        "content-type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    });
    if (res.ok) reload();
  }

  function reload() {
    setForm(InitialForm);
    setEditMode(false);
    setEditTransaction({});
    fetchTransactions();
  }

  return (
    <Card sx={{ maxWidth: 600, mx: "auto", mt: 4, p: 3, boxShadow: 3, borderRadius: 3 }}>
      <CardContent>
        <Typography variant="h5" sx={{ mb: 2, textAlign: "center", fontWeight: "bold", color: "primary.main" }}>
          {editMode ? "Update Transaction" : "Add New Transaction"}
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
          <TextField label="Amount (â‚¹)" type="number" name="amount" value={form.amount} onChange={handleChange} fullWidth variant="outlined" />
          <TextField label="Description" type="text" name="description" value={form.description} onChange={handleChange} fullWidth variant="outlined" />
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DesktopDatePicker label="Transaction Date" inputFormat="DD.MM.YYYY" value={form.date} onChange={handleDate} renderInput={(params) => <TextField {...params} fullWidth />} />
          </LocalizationProvider>
          <Autocomplete
            options={categories}
            getOptionLabel={(option) => option.name || ""}
            onChange={(event, newValue) => setForm({ ...form, category_id: newValue ? newValue._id : "" })}
            renderInput={(params) => <TextField {...params} label="Category" fullWidth />}
          />
          <Box sx={{ display: "flex", justifyContent: "space-between", mt: 2 }}>
            <Button type="submit" variant="contained" color="success" size="large">
              {editMode ? "Update" : "Submit"}
            </Button>
            {editMode && (
              <Button variant="outlined" color="error" onClick={handleCancel} size="large">
                Cancel
              </Button>
            )}
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
}
