import React from "react";
import { PieChart, Pie, Tooltip, Cell, ResponsiveContainer, Legend } from "recharts";
import dayjs from "dayjs";
import Paper from "@mui/material/Paper";

// Define Colors for Pie Segments
const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042", "#AF19FF", "#FF6666", "#66B2FF", "#FF99CC", "#99FF99", "#FFCC66"];

export default function TransactionChart({ data }) {
  // Format Data for Pie Chart
  const chartData = data.map((item, index) => ({
    name: dayjs().month(item._id - 1).format("MMMM"),
    value: item.totalExpenses,
    color: COLORS[index % COLORS.length], // Assign colors dynamically
  }));

  return (
    <Paper style={{ padding: "20px", marginTop: "20px" }}>
      <h3 style={{ textAlign: "center", marginBottom: "20px" }}>Monthly Expense Distribution</h3>
      <ResponsiveContainer width="100%" height={350}>
        <PieChart>
          <Pie
            data={chartData}
            dataKey="value"
            nameKey="name"
            cx="50%"
            cy="50%"
            outerRadius={120}
            fill="#8884d8"
            label
          >
            {chartData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))}
          </Pie>
          <Tooltip />
          <Legend verticalAlign="bottom" height={36} />
        </PieChart>
      </ResponsiveContainer>
    </Paper>
  );
}
