import * as React from "react";
import Paper from "@mui/material/Paper";
import { scaleBand } from "@devexpress/dx-chart-core";
import {
  Chart,
  ArgumentAxis,
  ValueAxis,
  BarSeries,
  Tooltip,
} from "@devexpress/dx-react-chart-material-ui";
import {
  Animation,
  ArgumentScale,
  EventTracker,
} from "@devexpress/dx-react-chart";
import dayjs from "dayjs";

export default function TransactionChart({ data }) {
  const chartData = data.map((item) => ({
    month: dayjs().month(item._id - 1).format("MMMM"),
    totalExpenses: item.totalExpenses,
  }));

  return (
    <Paper elevation={4} sx={{ padding: 3, borderRadius: "12px", background: "#f4f6f8" }}>
      <Chart data={chartData} height={350}>
        <ArgumentScale factory={scaleBand} />
        <ArgumentAxis showGrid showTicks />
        <ValueAxis showGrid showLine showTicks />
        <BarSeries
          valueField="totalExpenses"
          argumentField="month"
          color="#4CAF50"
          barWidth={0.6}
        />
        <Animation duration={700} />
        <EventTracker />
        <Tooltip
          contentComponent={({ targetItem }) => (
            <div style={{ padding: "8px", background: "white", borderRadius: "5px", boxShadow: "0px 4px 8px rgba(0,0,0,0.1)" }}>
              <strong>{chartData[targetItem.point].month}</strong>: â‚¹{chartData[targetItem.point].totalExpenses}
            </div>
          )}
        />
      </Chart>
    </Paper>
  );
}
