  const rangeStep = adjustBandScaleMoveStep(delta, step);
  if (rangeStep === 0) {
    return bounds;
  }
  const range = scaleBounds(scale, bounds);
  const range0 = Math.round((range[0] - fullRange[0]) / step);
  const range1 = range0 + Math.round((range[1] - range[0]) / step) - 1;
  let new0 = range0 + rangeStep;
  let new1 = range1 + rangeStep;
  if (new0 < 0) {
    new0 = 0;
    new1 = new0 + range1 - range0;
  }
  if (new1 > domain.length - 1) {
    new1 = domain.length - 1;
    new0 = new1 - range1 + range0;
  }
  if (new0 === range0 || new1 === range1) {
    return bounds;
  }
  return [domain[new0], domain[new1]];
};

// Defines how much linear scale can be zoomed it.
// I.e. if original scale domain has size of 1, then fully zoomed scale domain has size
// of 1 / LINEAR_SCALE_ZOOMING_THRESHOLD.
const LINEAR_SCALE_ZOOMING_THRESHOLD = 1000;

const growLinearScaleBounds = (
  scale: ScaleObject, bounds: DomainBounds, delta: number, anchor: number,
): DomainBounds => {
  const fullRange = scale.range();
  const minRangeThreshold = (fullRange[1] - fullRange[0]) / LINEAR_SCALE_ZOOMING_THRESHOLD;
  const sign = Math.sign(fullRange[1] - fullRange[0]);
  const range = scaleBounds(scale, bounds);
  // If zooming in and initial range is already too small then do nothing.
  if (delta > 0 && Math.abs(range[1] - range[0]) <= Math.abs(minRangeThreshold)) {
    return bounds;
  }
  // If zooming out and initial range is already too large then do nothing.
  if (delta < 0 && Math.abs(range[1] - range[0]) >= Math.abs(fullRange[1] - fullRange[0])) {
    return bounds;
  }
  const t = Math.abs((anchor - range[0]) / (range[1] - range[0]));
  let r0 = range[0] + sign * delta * 2 * t;
  let r1 = range[1] - sign * delta * 2 * (1 - t);
  // If new range is outside of the left border then clamp it.
  if (Math.sign(r0 - fullRange[0]) !== sign) {
    r0 = fullRange[0];
  }
  // If new range is outside of the right border then clamp it.
  if (Math.sign(fullRange[1] - r1) !== sign) {
    r1 = fullRange[1];
  }
  // If new range is too small then make it no less than minimal available.
  if (Math.sign(r1 - r0) !== sign || Math.abs(r1 - r0) < Math.abs(minRangeThreshold)) {
    if (Math.abs(r0 - range[0]) < Math.abs(minRangeThreshold / 2)) {
      // Dock it to the start.
      r0 = range[0];
      r1 = r0 + minRangeThreshold;
    } else if (Math.abs(r1 - range[1]) < Math.abs(minRangeThreshold / 2)) {
      // Dock it to the end.
      r1 = range[1];
      r0 = r1 - minRangeThreshold;
    } else {
      // Dock it to the anchor.
      r0 = anchor - minRangeThreshold / 2;
      r1 = anchor + minRangeThreshold / 2;
    }
  }
  const newBounds: DomainBounds = [scale.invert!(r0), scale.invert!(r1)];
  return rangesEqual(bounds, newBounds) ? bounds : newBounds;
};

const growBandScaleBounds = (
  scale: ScaleObject, bounds: DomainBounds, delta: number, anchor: number,
): DomainBounds => {
  const domain = scale.domain();
  const fullRange = scale.range();
  const step = (fullRange[1] - fullRange[0]) / domain.length;
  const range = scaleBounds(scale, bounds);
  const range0 = Math.round((range[0] - fullRange[0]) / step);
  const range1 = range0 + Math.round((range[1] - range[0]) / step) - 1;
  // Let it be always 1 for now.
  const rangeStep = Math.sign(delta);
  if (
    (rangeStep === 0) ||
    (rangeStep > 0 && range0 === range1) ||
    (rangeStep < 0 && range0 === 0 && range1 === domain.length - 1)
  ) {
    return bounds;
  }
  const t = Math.abs((anchor - range[0]) / (range[1] - range[0]));
  let new0 = range0 + Math.round(rangeStep * 2 * t);
  let new1 = range1 - Math.round(rangeStep * 2 * (1 - t));
  if (new0 < 0) {
    new0 = 0;
  }
  if (new1 > domain.length - 1) {
    new1 = domain.length - 1;
  }
  if (new0 > new1) {
    if (t <= 0.5) {
      new1 = new0;
    } else {
      new0 = new1;
    }
  }
  if (new0 === range0 && new1 === range1) {
    return bounds;
  }
  return [domain[new0], domain[new1]];
};

const invertLinearScaleBounds = (scale: ScaleObject, range: NumberArray): DomainBounds => {
  const fullRange = scale.range();
  const match = Math.sign(fullRange[1] - fullRange[0]) === Math.sign(range[1] - range[0]);
  return [
    scale.invert!(range[match ? 0 : 1]),
    scale.invert!(range[match ? 1 : 0]),
  ];
};

const matchPointToBand = (domain: DomainItems, range: NumberArray, p: number) => {
  const i = Math.floor(domain.length * (p - range[0]) / (range[1] - range[0]));
  return domain[Math.min(i, domain.length - 1)];
};

const invertBandScaleBounds = (scale: ScaleObject, range: NumberArray): DomainBounds => {
  const domain = scale.domain();
  const fullRange = scale.range();
  return [
    matchPointToBand(domain, fullRange, range[0]),
    matchPointToBand(domain, fullRange, range[1]),
  ];
};

// Though these functions are used only in *Viewport* plugin (and so should be placed right there),
// they reside here so that internal scale specifics (*getWidth*)
// are encapsulated in this utility file.
/** @internal */
export const scaleBounds = makeScaleHelper(scaleLinearBounds, scaleBandBounds);
/** @internal */
export const moveBounds = makeScaleHelper(moveLinearScaleBounds, moveBandScaleBounds);
// "scaleBounds" would be a better name but "scale" is already occupied.
/** @internal */
export const growBounds = makeScaleHelper(growLinearScaleBounds, growBandScaleBounds);
/** @internal */
export const invertBoundsRange = makeScaleHelper(invertLinearScaleBounds, invertBandScaleBounds);
import { extent } from 'd3-array';
import {
  getValueDomainName, scaleLinear, scaleBand, rangesEqual, makeScale,
} from '../../utils/scale';
import { ARGUMENT_DOMAIN, VALUE_DOMAIN } from '../../constants';
import {
  Series, PointList, DomainItems, DomainInfoCache, BuildScalesFn, DomainInfo, DomainOptions,
  AddDomainFn, MergeDomainsFn, GetItemFn, GetDomainItemsFn,
  FactoryFn, ExtendDomainsFn, NumberArray,
} from '../../types';

const makeDomain = ({ factory, modifyDomain }: DomainOptions): DomainInfo => ({
  domain: [],
  factory,
  isDiscrete: !!(factory && isDiscrete(factory)),
  modifyDomain,
});

/** @internal */
export const defaultDomains: DomainInfoCache = {
  [ARGUMENT_DOMAIN]: makeDomain({}),
  [VALUE_DOMAIN]: makeDomain({}),
};
/** @internal */
export const addDomain: AddDomainFn = (domains, name, options) => ({
  ...domains,
  [name]: makeDomain(options),
});

const mergeContinuousDomains: MergeDomainsFn = (domain, items) => {
  const newDomain = extent([...domain, ...items]);
  return rangesEqual(newDomain as any, domain as any) ? domain : newDomain;
};

const mergeDiscreteDomains: MergeDomainsFn = (domain, items) => {
  const newDomain = Array.from(new Set([...domain, ...items]));
  return newDomain.length === domain.length ? domain : newDomain;
};

const getArgument: GetItemFn = point => point.argument;
const getValue: GetItemFn = point => point.value;

const guessFactory = (points: PointList, getItem: GetItemFn) => (
  points.length && typeof getItem(points[0]) === 'string' ? scaleBand : scaleLinear
);

const isDiscrete = (factory: FactoryFn) => 'bandwidth' in factory();

const updateDomainFactory = (domain: DomainInfo, series: Series, getItem: GetItemFn) => {
  if (domain.factory) {
    return domain;
  }
  const factory = guessFactory(series.points, getItem);
  return {
    ...domain,
    factory,
    isDiscrete: isDiscrete(factory),
  };
};

/** @internal */
export const updateDomainItems = (domain: DomainInfo, items: DomainItems): DomainInfo => {
  const merge = domain.isDiscrete ? mergeDiscreteDomains : mergeContinuousDomains;
  const merged = merge(domain.domain, items);
  return merged === domain.domain ? domain : {
    ...domain,
    domain: domain.modifyDomain ? domain.modifyDomain(merged) : merged,
  };
};

const getArgumentDomainItems: GetDomainItemsFn = series => series.points.map(getArgument);

const getValueDomainItems: GetDomainItemsFn = (series) => {
  const items = series.points.map(getValue);
  return series.getPointTransformer.isStartedFromZero ? [0, ...items] : items;
};

const updateDomain = (
  domain: DomainInfo, series: Series, getItem: GetItemFn, getDomainItems: GetDomainItemsFn,
) => updateDomainItems(updateDomainFactory(domain, series, getItem), getDomainItems(series));

/** @internal */
export const extendDomains: ExtendDomainsFn = (domains, series) => {
  const argumentDomain = updateDomain(
    domains[ARGUMENT_DOMAIN], series, getArgument, getArgumentDomainItems);
  const valueDomainName = getValueDomainName(series.scaleName);
  const valueDomain = updateDomain(
    domains[valueDomainName], series, getValue, getValueDomainItems);
  const changes = {};
  if (argumentDomain !== domains[ARGUMENT_DOMAIN]) {
    changes[ARGUMENT_DOMAIN] = argumentDomain;
  }
  if (valueDomain !== domains[valueDomainName]) {
    changes[valueDomainName] = valueDomain;
  }
  return Object.keys(changes).length ? { ...domains, ...changes } : domains;
};

/** @internal */
export const buildScales: BuildScalesFn = (domains, ranges) => {
  const scales = {};
  Object.keys(domains).forEach((name) => {
    scales[name] = makeScale(
      domains[name],
      ranges[name === ARGUMENT_DOMAIN ? ARGUMENT_DOMAIN : VALUE_DOMAIN] as NumberArray,
    );
  });
  return scales;
};
import { Size } from '@devexpress/dx-react-core';
import {
  ARGUMENT_DOMAIN, VALUE_DOMAIN,
} from '../../constants';
import {
  BBoxes, BBoxesChange, RangesCache, NumberArray,
} from '../../types';

const isEqual = (
  { width: firstWidth, height: firstHeight }: Size,
  { width: secondWidth, height: secondHeight }: Size,
) => firstWidth === secondWidth && firstHeight === secondHeight;

/** @internal */
export const bBoxes = (prevBBoxes: BBoxes, { bBox, placeholder }: BBoxesChange) => {
  if (isEqual(prevBBoxes[placeholder] || {}, bBox)) return prevBBoxes;
  return { ...prevBBoxes, [placeholder]: bBox };
};

/** @internal */
export const getRanges = (paneSize: Size, rotated: boolean): RangesCache => {
  const horRange: NumberArray = [0, paneSize.width];
  const verRange: NumberArray = [paneSize.height, 0];
  return {
    [ARGUMENT_DOMAIN]: rotated ? verRange : horRange,
    [VALUE_DOMAIN]: rotated ? horRange : verRange,
  };
};
import { isHorizontal } from '../../utils/scale';
import {
  LEFT, RIGHT, TOP, BOTTOM, MIDDLE, END, START,
} from '../../constants';
import {
  ScaleObject, GetFormatFn, ProcessTickFn, TickFormatFn, NumberArray, GetTickCoordinatesFn,
  TickCoordinatesGetterFn, Tick, Grid,
} from '../../types';

const getTicks = (scale: ScaleObject, count: number) => (
  scale.ticks ? scale.ticks(count) : scale.domain()
);

const createTicks = <T>(
  scale: ScaleObject, count: number, callback: ProcessTickFn<T>,
): ReadonlyArray<T> => (
  getTicks(scale, count)
    .map((tick, index) => callback(scale(tick), String(index), tick))
);

const getFormat = (scale: ScaleObject, count: number, tickFormat?: TickFormatFn): GetFormatFn => {
  if (scale.tickFormat) {
    return tickFormat ? tickFormat(scale, count) : scale.tickFormat(count);
  }
  return tick => tick;
};

const rotatedPositions = {
  [LEFT]: BOTTOM,
  [RIGHT]: TOP,
  [BOTTOM]: LEFT,
  [TOP]: RIGHT,
};

const positionFlags = {
  [LEFT]: false,
  [RIGHT]: false,
  [BOTTOM]: true,
  [TOP]: true,
};

/** @internal */
export const getRotatedPosition = (position: string) => rotatedPositions[position];

/** @internal */
export const isValidPosition = (position: string, scaleName: string, rotated: boolean) =>
  positionFlags[position] === isHorizontal(scaleName, rotated);

const createHorizontalOptions = (position: string, tickSize: number, indentFromAxis: number) => {
  // Make *position* orientation agnostic - should be START or END.
  const isStart = position === BOTTOM;
  return {
    y1: 0,
    y2: isStart ? +tickSize : -tickSize,
    yText: isStart ? +indentFromAxis : -indentFromAxis,
    dy: isStart ? '1em' : '0em',
    textAnchor: MIDDLE,
  };
};

const createVerticalOptions = (position: string, tickSize: number, indentFromAxis: number) => {
  // Make *position* orientation agnostic - should be START or END.
  const isStart = position === LEFT;
  return {
    x1: 0,
    x2: isStart ? -tickSize : +tickSize,
    xText: isStart ? -indentFromAxis : +indentFromAxis,
    dy: '0.3em',
    textAnchor: isStart ? END : START,
  };
};

// Constant is selected to preserve original behavior described in
// https://github.com/d3/d3-scale#continuous_ticks.
const DEFAULT_TICK_COUNT = 10;
const getTickCount = (scaleRange: NumberArray, paneSize: number) => {
  const rangeToPaneRatio = Math.abs(scaleRange[0] - scaleRange[1]) / paneSize;
  return Math.round(DEFAULT_TICK_COUNT * (isFinite(rangeToPaneRatio) ? rangeToPaneRatio : 1));
};

const createTickFilter = (isHor: boolean, size: number) => (
  isHor
    ? (tick: any) => tick.x1 >= 0 && tick.x1 <= size
    : (tick: any) => tick.y1 >= 0 && tick.y1 <= size
);

/** @internal */
export const tickCoordinatesGetter: TickCoordinatesGetterFn<Tick> = ({
  isHor, scale, tickCount, tickFormat, position, tickSize, indentFromAxis,
}) => {
  const formatTick = getFormat(scale!, tickCount!, tickFormat);
  const options = (isHor ? createHorizontalOptions : createVerticalOptions)(
    position!, tickSize!, indentFromAxis!,
  );
  return (coordinates, key, tick) => ({
    key,
    x1: coordinates,
    x2: coordinates,
    y1: coordinates,
    y2: coordinates,
    xText: coordinates,
    yText: coordinates,
    text: formatTick(tick),
    ...options,
  });
};

/** @internal */
export const gridCoordinatesGetter: TickCoordinatesGetterFn<Grid> = ({ isHor }) => {
  const options = isHor ? { y1: 0 } : { x1: 0 };
  return (coordinates, key) => ({
    key,
    x1: coordinates,
    y1: coordinates,
    ...options,
  });
};

/** @internal */
export const getTickCoordinates: GetTickCoordinatesFn<Tick | Grid> = ({
  scaleName, scale, paneSize, rotated, callback, ...restProps
}) => {
  const isHor = isHorizontal(scaleName, rotated);
  const tickCount = getTickCount(scale.range(), paneSize[1 - Number(isHor)]);
  const ticks = createTicks(scale, tickCount, callback({ isHor, scale, tickCount, ...restProps }));
  const visibleTicks = ticks.filter(createTickFilter(isHor, paneSize[1 - Number(isHor)]));
  return {
    ticks: visibleTicks,
    sides: [Number(isHor), Number(!isHor)],
  };
};
import {
  symbol,
  symbolCircle,
  line,
  curveMonotoneX,
  curveMonotoneY,
  area,
  arc,
  pie,
} from 'd3-shape';
import {
  SeriesList, Series, PointList, Point, DataItems, AddSeriesFn, ScalesCache, ScaleSeriesPointsFn,
  GetPointTransformerFn, Colors, Rect,
  BarSeries, ScatterSeries, PieSeries,
  PointComponentProps, PathFn, PathStartCoordinates, PathEndCoordinates, Scales,
} from '../../types';
import { Size } from '@devexpress/dx-react-core';
import { ARGUMENT_DOMAIN } from '../../constants';
import { getValueDomainName, getWidth, rangesEqual } from '../../utils/scale';

const getArg = ({ arg }: PointComponentProps) => arg;
const getVal = ({ val }: PointComponentProps) => val;
const getStartVal = ({ startVal }: PointComponentProps) => startVal!;

/** @internal */
export const dArea: PathFn = area<PointComponentProps>()
  .x(getArg)
  .y1(getVal)
  .y0(getStartVal) as any;

/** @internal */
export const dRotateArea: PathFn = area<PointComponentProps>()
  .x1(getStartVal)
  .x0(getVal)
  .y(getArg) as any;

/** @internal */
export const dLine: PathFn = line<PointComponentProps>()
  .x(getArg)
  .y(getVal) as any;

/** @internal */
export const dRotateLine = line<PointComponentProps>()
  .x(getVal)
  .y(getArg) as any;

/** @internal */
export const dSpline: PathFn = line<PointComponentProps>()
  .x(getArg)
  .y(getVal)
  .curve(curveMonotoneX) as any;

/** @internal */
export const dRotateSpline: PathFn = line<PointComponentProps>()
  .x(getVal)
  .y(getArg)
  .curve(curveMonotoneY) as any;

/** @internal */
export const dBar = (
  arg: number, val: number, startVal: number, width: number, rotated: boolean,
) => {
  const height = Math.abs(val - startVal!);
  const minVal = Math.min(val, startVal!);
  return {
    x: rotated ? minVal : arg - width / 2,
    y: rotated ? arg - width / 2 : minVal,
    width: rotated ? height : width || 2,
    height: rotated ? width || 2 : height,
  };
};

/** @internal */
export const getPiePointTransformer: GetPointTransformerFn = ({
  argumentScale, valueScale, points,
}) => {
  const x = Math.max(...argumentScale.range()) / 2;
  const y = Math.max(...valueScale.range()) / 2;
  const maxRadius = Math.min(x, y);
  const pieData = pie<Point>().sort(null).value(d => d.value)(points as Point[]);
  return (point) => {
    const { startAngle, endAngle } = pieData[point.index];
    return {
      ...point,
      arg: x,
      val: y,
      startAngle,
      endAngle,
      maxRadius,
    };
  };
};

/** @internal */
export const getLinePointTransformer: GetPointTransformerFn = ({
  argumentScale, valueScale,
}) => point => ({
  ...point,
  arg: argumentScale(point.argument),
  val: valueScale(point.value),
} as any);

// Though transformations for line and scatter are the same,
// separate function instance is required as it contains additional static fields.
/** @internal */
export const getScatterPointTransformer: GetPointTransformerFn = (
  ...args
) => getLinePointTransformer(...args);

/** @internal */
export const getAreaPointTransformer: GetPointTransformerFn = (series) => {
  const transform = getLinePointTransformer(series);
  const startVal = series.valueScale(0);
  return (point) => {
    const ret = transform(point);
    return {
      ...ret,
      startVal,
    };
  };
};
// Used for domain calculation and stacking.
getAreaPointTransformer.isStartedFromZero = true;

/** @internal */
export const getBarPointTransformer: GetPointTransformerFn = ({
  argumentScale, valueScale,
}) => {
  const startVal = valueScale(0);
  return point => ({
    ...point,
    arg: argumentScale(point.argument),
    val: valueScale(point.value),
    startVal,
    maxBarWidth: getWidth(argumentScale),
  } as any);
};
// Used for domain calculation and stacking.
getBarPointTransformer.isStartedFromZero = true;
// Used for Bar grouping.
getBarPointTransformer.isBroad = true;

getPiePointTransformer.getPointColor = (palette, index) => palette[index % palette.length];

/** @internal */
export const findSeriesByName = (
  name: symbol, series: SeriesList,
): Series => series.find(seriesItem => seriesItem.symbolName === name) as Series;

/** @internal */
export const dSymbol = (
  { size }: ScatterSeries.PointOptions,
) => symbol().size(size ** 2).type(symbolCircle)()!;

/** @internal */
export const dPie = (
  maxRadius: number, innerRadius: number, outerRadius: number, startAngle: number, endAngle: number,
) => arc()({
  startAngle,
  endAngle,
  innerRadius: innerRadius * maxRadius,
  outerRadius: outerRadius * maxRadius,
})!;

const getRect = (
  cArg: number, cVal: number, dArg: number, dVal: number, rotated: boolean,
): Rect => {
  const minArg = cArg - dArg;
  const minVal = cVal - dVal;
  const maxArg = cArg + dArg;
  const maxVal = cVal + dVal;
  return rotated ? [minVal, minArg, maxVal, maxArg] : [minArg, minVal, maxArg, maxVal];
};

getBarPointTransformer.getTargetElement = (point) => {
  const {
    arg, val, startVal, barWidth, maxBarWidth, rotated,
  } = point as BarSeries.PointProps;
  const halfWidth = barWidth * maxBarWidth / 2;
  const halfHeight = Math.abs(startVal! - val) / 2;
  const centerVal = (val + startVal!) / 2;
  return getRect(arg, centerVal, halfWidth, halfHeight, rotated);
};

getPiePointTransformer.getTargetElement = (point) => {
  const {
    arg: x, val: y, innerRadius, outerRadius, maxRadius, startAngle, endAngle,
  } = point as PieSeries.PointProps;
  const center = arc().centroid({
    startAngle,
    endAngle,
    innerRadius: innerRadius * maxRadius,
    outerRadius: outerRadius * maxRadius,
  });
  const cx = center[0] + x;
  const cy = center[1] + y;
  return getRect(cx, cy, 0.5, 0.5, false);
};

getAreaPointTransformer.getTargetElement = ({ arg, val, rotated }) => (
  getRect(arg, val, 1, 1, rotated)
);

getLinePointTransformer.getTargetElement = getAreaPointTransformer.getTargetElement;

getScatterPointTransformer.getTargetElement = (obj) => {
  const { arg, val, point, rotated } = obj as ScatterSeries.PointProps;
  const t = point.size / 2;
  return getRect(arg, val, t, t, rotated);
};

const getUniqueName = (list: SeriesList, name: string) => {
  const names = new Set(list.map(item => item.name));
  let ret = name;
  while (names.has(ret)) {
    // @ts-ignore
    ret = ret.replace(/\d*$/, str => (str ? +str + 1 : 0));
  }
  return ret;
};

// TODO: Memoization is much needed here.
// Though "series" list never persists, single "series" item most often does.
const createPoints = (
  {
    argumentField, valueField, getPointTransformer,
  }: Series,
  data: DataItems, props: any, palette: Colors,
): PointList => {
  const points: Point[] = [];
  data.forEach((dataItem, index) => {
    const argument = dataItem[argumentField];
    const value = dataItem[valueField];
    if (argument !== undefined && value !== undefined) {
      points.push({
        argument,
        value,
        index,
        ...props,
        color: getPointTransformer.getPointColor
          ? getPointTransformer.getPointColor(palette, index) : props.color,
      });
    }
  });
  return points;
};

/** @internal */
export const addSeries: AddSeriesFn = (
  series, data, palette, props, restProps,
) => {
  // It is used to generate unique series dependent attribute names for patterns.
  // *symbolName* cannot be used as it cannot be part of DOM attribute name.
  const index = series.length;
  const seriesColor = props.color || palette[index % palette.length];
  return [...series, {
    ...props,
    index,
    name: getUniqueName(series, props.name),
    points: createPoints(props, data, { ...restProps, color: seriesColor }, palette),
    color: seriesColor,
  }];
};

// TODO: Memoization is much needed here by the same reason as in "createPoints".
// Make "scales" persistent first.
const scalePoints = (series: Series, scales: ScalesCache, rotated: boolean) => {
  const transform = series.getPointTransformer({
    ...series,
    argumentScale: scales[ARGUMENT_DOMAIN],
    valueScale: scales[getValueDomainName(series.scaleName)],
  });
  const ret: Series = {
    ...series,
    rotated,
    points: series.points.map(point => ({
      ...transform(point),
      rotated,
    })),
  };
  return ret;
};

/** @internal */
export const scaleSeriesPoints: ScaleSeriesPointsFn = (
  series, scales, rotated,
) => series.map(seriesItem => scalePoints(seriesItem, scales, rotated));

/** @internal */
export const getVisibility = (
  pane: Size, centerX: number, centerY: number, width: number, height: number,
) => {
  return (centerX - width / 2 < 0 && centerX + width / 2 < 0)
  || (centerX - width / 2 > pane.width && centerX + width / 2 > pane.width)
  || (centerY - height / 2 < 0 && centerY + height / 2 < 0)
  || (centerY - height / 2 > pane.height && centerY + height / 2 > pane.height) ?
  'hidden' : 'visible';
};

/** @internal */
export const adjustBarSize = (
  bar: { width: number, height: number, x: number, y: number }, { width, height }: Size,
) => {
  const x = Math.max(0, bar.x);
  const y = Math.max(0, bar.y);
  return {
    x,
    y,
    width: Math.min(width, bar.x + bar.width) - x,
    height: Math.min(height, bar.y + bar.height) - y,
  };
};

/** @internal */
export const isValuesChanged = (previous: any, current: any): boolean => {
  return Object.entries(previous).some(el => el[1] !== current[el[0]]);
};

/** @internal */
export const isCoordinatesChanged = (
  { coordinates: prevCoordinates }: PathStartCoordinates, { coordinates }: PathEndCoordinates,
) => {
  if (prevCoordinates.length !== coordinates.length) {
    return true;
  }
  return prevCoordinates.some((el, index) => {
    return el.arg !== coordinates[index].arg || el.val !== coordinates[index].val;
  });
};

/** @internal */
export const isScalesChanged = (previous: Scales, current: Scales): boolean => {
  return !rangesEqual(previous.argScale.range(), current.argScale.range()) ||
  !rangesEqual(previous.valScale.range(), current.valScale.range());
};
import { stack } from 'd3-shape';
import { scaleBand } from 'd3-scale';
import {
  SeriesList, Series, Point, GetPointTransformerFn, DataItems, DomainInfoCache,
  StackList, GetPointTransformerFnRaw, BarSeries, StackedPoint, StackMap, GetStackedSeriesFn,
  SeriesPositions, StacksKeys, StackedDataItems, OffsetFn, OrderFn,
  StackedData, GetStackedDomainsFn,
} from '../../types';
import { extendDomains, updateDomainItems } from '../scale/computeds';
import { getValueDomainName } from '../../utils/scale';

// "Stack" plugin relies on "data" and "series" getters and
// knowledge about "getPointTransformer" and "path" functions behavior.
const buildSeriesToStackMap = (stacks: StackList): StackMap => {
  const result = {};
  stacks.forEach(({ series }, i) => {
    series.forEach((name) => {
      result[name] = i;
    });
  });
  return result;
};

const getStackedPointTransformer = (getPointTransformer: GetPointTransformerFn) => {
  const wrapper: GetPointTransformerFnRaw = (series) => {
    const transform = getPointTransformer(series);
    const { valueScale } = series;
    return (point: Point) => {
      const ret = transform(point);
      return {
        ...ret,
        startVal: valueScale((point as StackedPoint).value0),
      };
    };
  };
  // Preserve static fields of original transformer.
  Object.assign(wrapper, getPointTransformer);
  return wrapper as GetPointTransformerFn;
};

const collectStacks = (
  seriesList: SeriesList, seriesToStackMap: StackMap,
  stacksKeys: StacksKeys, seriesPositions: SeriesPositions,
) => {
  seriesList.forEach(({ name, valueField }) => {
    const stackId = seriesToStackMap[name];
    if (stackId === undefined) {
      return;
    }

    if (!stacksKeys[stackId]) {
      stacksKeys[stackId] = [];
    }
    seriesPositions[name] = stacksKeys[stackId].length;
    stacksKeys[stackId].push(valueField);
  });
  // Stack cannot consist of single series.
  Object.keys(stacksKeys).forEach((stackId) => {
    if (stacksKeys[stackId].length === 1) {
      delete stacksKeys[stackId];
    }
  });
};

const getStackedData = (
  stacksKeys: StacksKeys, dataItems: DataItems, offset: OffsetFn, order: OrderFn,
): StackedData => {
  const result = {};
  Object.keys(stacksKeys).forEach((stackId) => {
    result[stackId] = stack().keys(stacksKeys[stackId])
      .order(order as any)
      .offset(offset as any)(dataItems as any);
  });
  return result;
};

const buildStackedSeries = (series: Series, dataItems: StackedDataItems): Series => {
  const points = series.points.map((point) => {
    const [value0, value] = dataItems[point.index];
    return { ...point, value, value0 };
  });
  const stackedSeries = {
    ...series,
    points,
    isStacked: true,
  };
  if (series.getPointTransformer.isStartedFromZero) {
    stackedSeries.getPointTransformer = getStackedPointTransformer(series.getPointTransformer);
  }
  return stackedSeries;
};

const applyStacking = (
  seriesList: SeriesList, dataItems: DataItems, seriesToStackMap: StackMap,
  offset: OffsetFn, order: OrderFn,
): SeriesList => {
  const stacksKeys: StacksKeys = {};
  const seriesPositions: SeriesPositions = {};
  collectStacks(seriesList, seriesToStackMap, stacksKeys, seriesPositions);
  if (Object.keys(stacksKeys).length === 0) {
    return seriesList;
  }
  const stackedData = getStackedData(stacksKeys, dataItems, offset, order);
  return seriesList.map((seriesItem) => {
    const stackId = seriesToStackMap[seriesItem.name];
    const stackData = stackedData[stackId];
    if (!stackData) {
      return seriesItem;
    }
    const position = seriesPositions[seriesItem.name];
    return buildStackedSeries(seriesItem, stackData[position]);
  });
};

const getGroupName = (series: Series, i: number, seriesToStackMap: StackMap) => {
  const stackId = seriesToStackMap[series.name];
  return stackId >= 0 ? String(stackId) : `group-${i}`;
};

const getGroupedPointTransformer = (
  getPointTransformer: GetPointTransformerFn, groupCount: number, groupOffset: number,
) => {
  const wrapper: GetPointTransformerFnRaw = (series) => {
    const transform = getPointTransformer(series);
    const widthCoeff = 1 / groupCount;
    return (point) => {
      const original = transform(point) as BarSeries.PointProps;
      const arg = (
        original.arg - original.maxBarWidth * (0.5 - 0.5 * widthCoeff - groupOffset * widthCoeff)
      );
      const result: BarSeries.PointProps = {
        ...original,
        arg,
        maxBarWidth: original.maxBarWidth / groupCount,
      };
      return result;
    };
  };
  // Preserve static fields of original transformer.
  Object.assign(wrapper, getPointTransformer);
  return wrapper as GetPointTransformerFn;
};

const applyGrouping = (seriesList: SeriesList, seriesToStackMap: StackMap): SeriesList => {
  const groups = new Set<string>();
  seriesList.forEach((seriesItem, i) => {
    if (seriesItem.getPointTransformer.isBroad) {
      groups.add(getGroupName(seriesItem, i, seriesToStackMap));
    }
  });
  // There cannot be single group.
  if (groups.size < 2) {
    return seriesList;
  }
  const scale = scaleBand().domain(Array.from(groups)).range([0, groups.size]);
  return seriesList.map((seriesItem, i) => {
    if (!seriesItem.getPointTransformer.isBroad) {
      return seriesItem;
    }
    const getPointTransformer = getGroupedPointTransformer(
      seriesItem.getPointTransformer,
      groups.size,
      scale(getGroupName(seriesItem, i, seriesToStackMap))!,
    );
    return {
      ...seriesItem,
      getPointTransformer,
    };
  });
};

/** @internal */
export const getStackedSeries: GetStackedSeriesFn = (
  seriesList, dataItems, { stacks, offset, order },
) => {
  const map = buildSeriesToStackMap(stacks);
  const stackedSeriesList = applyStacking(seriesList, dataItems, map, offset, order);
  const groupedSeriesList = applyGrouping(stackedSeriesList, map);
  return groupedSeriesList;
};

const resetDomainItems = (domains: DomainInfoCache): DomainInfoCache => {
  const result = {};
  Object.keys(domains).forEach((key) => {
    result[key] = { ...domains[key], domain: [] };
  });
  return result;
};

const extendDomainsWithAdditionalItems = (domains: DomainInfoCache, series: Series) => {
  const items = series.points.map(point => (point as StackedPoint).value0);
  const key = getValueDomainName(series.scaleName);
  const domain = updateDomainItems(domains[key], items);
  return domain !== domains[key] ? { ...domains, [key]: domain } : domains;
};

// Stacking changes data - so computed domains have to be discarded
// and recalculated from the new stacked data.
/** @internal */
export const getStackedDomains: GetStackedDomainsFn = (domains, seriesList) => {
  const stackedSeries = seriesList.filter(series => (series as any).isStacked);
  if (!stackedSeries.length) {
    return domains;
  }
  // Recalculate domains in a common way.
  const rebuiltDomains = seriesList.reduce(extendDomains, resetDomainItems(domains));
  // Take additional "value0" fields into account.
  return stackedSeries.reduce(extendDomainsWithAdditionalItems, rebuiltDomains);
};
import {
  Scales, PointComponentProps, PieSeries, PathComponentProps, GetDelayFn,
} from '../../types';

/** @internal */
export const easeOutCubic = (t: number) => (t - 1) * (t - 1) * (t - 1) + 1;

/** @internal */
export const getDelay: GetDelayFn = (index, isStart) => isStart ? index * 30 : 0;

/** @internal */
export const getStartVal = (scales: Scales) => {
  return scales.valScale.copy().clamp!(true)(0);
};

/** @internal */
export const getPathStart = (scales: Scales, { coordinates }: PathComponentProps) => {
  const start = getStartVal(scales);
  return { coordinates: coordinates.map(coord =>
    ({ arg: coord.arg, val: start, startVal: start })) };
};

/** @internal */
export const getPointStart = (scales: Scales, { arg }: PointComponentProps) => {
  const start = getStartVal(scales);
  return { arg, val: start, startVal: start };
};

/** @internal */
export const getPieStart = (scales: Scales, { startAngle, endAngle }: PieSeries.PointProps) =>
({ innerRadius: 0, outerRadius: 0, startAngle, endAngle });
import { SeriesRef, TargetList, NotifyPointerMoveFn, ProcessedTarget } from '../types';

// Comparing by reference is not an option as Tracker always sends new objects.
// Tracker cannot persist references as it actually operates with simple scalars
// and constructs objects to provide info in a slightly more suitable way.
const compareTargets = (target1: SeriesRef, target2: SeriesRef) => (
  target1.series === target2.series && target1.point === target2.point
);

// If *currentTarget* is among *targets* then it has priority but only while its distance
// is not significantly greater (DISTANCE_PRIORITY_RATIO) than that of the best candidate.
const selectTarget = (targets: TargetList, currentTarget: SeriesRef): ProcessedTarget => {
  const candidate = targets[0];
  if (!currentTarget) {
    return candidate;
  }
  if (!candidate) {
    return null;
  }
  return compareTargets(candidate, currentTarget) ? undefined : candidate;
};

/** @internal */
export const processPointerMove = (
  targets: TargetList, currentTarget: SeriesRef, notify?: NotifyPointerMoveFn,
) => {
  const nextTarget = selectTarget(targets, currentTarget);
  if (nextTarget === undefined) {
    return undefined;
  }
  if (notify) {
    notify(nextTarget);
  }
  return nextTarget;
};
import { NumberArray, Location } from '../types';
// This function is to be called from context where DOM is available -
// so *window* can be accessed safely.
/** @internal */
export const getOffset = (element: Element): NumberArray => {
  const { left, top } = element.getBoundingClientRect();
  // Safer way to get window.
  const { defaultView } = element.ownerDocument!;
  const { pageXOffset, pageYOffset } = defaultView!;
  return [left + pageXOffset, top + pageYOffset];
};

/** @internal */
export const getEventCoords = (e: any, offset: NumberArray): Location => {
  const { pageX, pageY } = e.touches ? e.touches[0] : e;
  return [pageX - offset[0], pageY - offset[1]];
};
import { processPointerMove } from '../../utils/hover-state';
import { getOffset } from '../../utils/common';
import {
  SeriesList, SeriesRef, TransformedPoint, TargetList, NotifyPointerMoveFn,
  TooltipParameters, TooltipReference, Rect,
} from '../../types';

/** @internal */
export const getParameters = (series: SeriesList, target: SeriesRef): TooltipParameters => {
  const currentSeries = series.find(({ name }) => target.series === name)!;
  const item = currentSeries.points.find(point => point.index === target.point) as TransformedPoint;
  return {
    element: currentSeries.getPointTransformer.getTargetElement(item),
    text: `${item.value}`,
  };
};

/** @internal */
export const createReference = (
  rect: Rect, rootRef: React.RefObject<Element>,
): TooltipReference => ({
  // These two fields together with *width* and *height* are left with stub data for
  // simplicity reasons - they seem to be unused by *Popper*.
  clientWidth: 0,
  clientHeight: 0,
  getBoundingClientRect() {
    // This function is expected to be called (by the *Popper*) when DOM is ready -
    // so *rootRef.current* can be accessed.
    const offset = getOffset(rootRef.current!);
    // *getBoundingClientRect* of a real html element is affected by window scrolling.
    // *popper.js* subscribes "html -> getBoundingClientRect -> (left, top)" from
    // "reference -> getBoundingClientRect" - so here it is added.
    const htmlRect = rootRef.current!.ownerDocument!.documentElement.getBoundingClientRect();
    const left = rect[0] + offset[0] + htmlRect.left;
    const right = rect[2] + offset[0] + htmlRect.left;
    const top = rect[1] + offset[1] + htmlRect.top;
    const bottom = rect[3] + offset[1] + htmlRect.top;
    return {
      left,
      top,
      right,
      bottom,
      width: right - left,
      height: bottom - top,
    };
  },
});

/** @internal */
export const processHandleTooltip = (
  targets: TargetList, currentTarget: SeriesRef, onTargetItemChange?: NotifyPointerMoveFn,
) => {
  const filterTargets = targets.filter(target => target.point !== undefined);
  return processPointerMove(filterTargets, currentTarget, onTargetItemChange);
};
import {
  ARGUMENT_DOMAIN, VALUE_DOMAIN,
} from '../../constants';
import {
  getValueDomainName, makeScale, scaleBounds, rangesEqual,
  moveBounds, growBounds, invertBoundsRange,
} from '../../utils/scale';
import {
  NumberArray,
  Viewport,
  ScalesCache,
  DomainInfoCache,
  RangesCache,
  DomainInfo,
  DomainBounds,
  OnViewportChangeFn,
  ScaleObject,
  EventHandlers,
  Location,
  Interaction,
} from '../../types';
import { Size } from '@devexpress/dx-react-core';

const getArgumentBounds = (viewport?: Viewport): DomainBounds | null => (
  viewport && viewport.argumentStart !== undefined && viewport.argumentEnd !== undefined
    ? [viewport.argumentStart, viewport.argumentEnd] : null
);

const getValueBounds = (viewport?: Viewport): DomainBounds | null => (
  viewport && viewport.valueStart !== undefined && viewport.valueEnd !== undefined
    ? [viewport.valueStart, viewport.valueEnd] : null
);

const getValueScaleName = (viewport?: Viewport) => (
  getValueDomainName(viewport && viewport.scaleName)
);

const getDefaultBounds = (scale: ScaleObject): DomainBounds => {
  const domain = scale.domain();
  return [domain[0], domain[domain.length - 1]];
};

const proportionallyExtendRange = (range: NumberArray, subRange: NumberArray): NumberArray => {
  const p = (subRange[0] - subRange[1]) / (range[0] - range[1]);
  const q = subRange[0] - p * range[0];
  return [
    (range[0] - q) / p,
    (range[1] - q) / p,
  ];
};

const adjustRange = (domain: DomainInfo, bounds: DomainBounds, range: NumberArray) => {
  const scale = makeScale(domain, range);
  const subRange = scaleBounds(scale, bounds);
  return rangesEqual(subRange, range) ? range : proportionallyExtendRange(range, subRange);
};

const update = (
  ranges: RangesCache, changes: any, key: string, domain: DomainInfo, bounds: DomainBounds,
) => {
  const newRange = adjustRange(domain, bounds, ranges[key]);
  if (newRange !== ranges[key]) {
    changes[key] = newRange;
  }
};

/** @internal */
export const adjustLayout = (
  domains: DomainInfoCache, ranges: RangesCache, viewport?: Viewport,
) => {
  const changes = {};
  const argumentBounds = getArgumentBounds(viewport);
  if (argumentBounds) {
    update(ranges, changes, ARGUMENT_DOMAIN, domains[ARGUMENT_DOMAIN], argumentBounds);
  }
  const valueBounds = getValueBounds(viewport);
  if (valueBounds) {
    update(ranges, changes, VALUE_DOMAIN, domains[getValueScaleName(viewport)], valueBounds);
  }
  return Object.keys(changes).length ? { ...ranges, ...changes } : ranges;
};

const boundsForScale = (
  name: string, scales: ScalesCache, currentBounds: DomainBounds | null,
  interaction: Interaction, type: Interaction, delta: number, anchor: number, range?: NumberArray,
): DomainBounds | null => {
  if (!checkInteraction(interaction, type)) {
    return null;
  }
  const scale = scales[name];
  const bounds = currentBounds || getDefaultBounds(scale);
  let newBounds: DomainBounds;
  if (type === 'pan') {
    newBounds = moveBounds(scale, bounds, delta);
  } else if (type === 'zoom') {
    newBounds = range ? invertBoundsRange(scale, range) : growBounds(scale, bounds, delta, anchor);
  }
  return newBounds! !== bounds ? newBounds! : null;
};

/** @internal */
export const getViewport = (
  scales: ScalesCache,
  rotated: boolean,
  [argInteraction, valInteraction]: Readonly<[Interaction, Interaction]>, type: Interaction,
  deltas: Readonly<[number, number]> | null,
  anchors: Readonly<number[]> | null,
  ranges: Readonly<[NumberArray, NumberArray]> | null,
  viewport?: Viewport, onViewportChange?: OnViewportChangeFn,
) => {
  const argIndex = Number(rotated);
  const valIndex = 1 - argIndex;
  const changes: any = {};
  const argumentBounds = boundsForScale(
    ARGUMENT_DOMAIN, scales, getArgumentBounds(viewport),
    argInteraction, type,
    deltas ? deltas[argIndex] : 0, anchors ? anchors[argIndex] : 0,
    ranges ? ranges[argIndex] : undefined,
  );
  const valueBounds = boundsForScale(
    getValueScaleName(viewport), scales, getValueBounds(viewport),
    valInteraction, type,
    deltas ? deltas[valIndex] : 0, anchors ? anchors[valIndex] : 0,
    ranges ? ranges[valIndex] : undefined,
  );
  if (argumentBounds) {
    changes.argumentStart = argumentBounds[0];
    changes.argumentEnd = argumentBounds[1];
  }
  if (valueBounds) {
    changes.valueStart = valueBounds[0];
    changes.valueEnd = valueBounds[1];
  }
  if (Object.keys(changes).length) {
    const newViewport = { ...viewport, ...changes };
    if (onViewportChange) {
      onViewportChange(newViewport);
    }
    return { viewport: newViewport };
  }
  return null;
};

/** @internal */
export const getDeltaForTouches = (touches: Touch[]) => {
  const deltaX = touches[0].pageX - touches[1].pageX;
  const deltaY = touches[0].pageY - touches[1].pageY;
  const delta = Math.sqrt(deltaX * deltaX + deltaY * deltaY);
  const center: NumberArray = [
    (touches[0].pageX + touches[1].pageX) / 2,
    (touches[0].pageY + touches[1].pageY) / 2,
  ];
  return { delta, center };
};

/** @internal */
export const isKeyPressed = (event: MouseEvent, key: string) => event[`${key}Key`];

/** @internal */
export const getWheelDelta = ({ wheelDelta, deltaY }: { wheelDelta?: number, deltaY?: number }) => {
  return wheelDelta !== undefined ? wheelDelta : deltaY! * -30; // deltaY for FF
};

/** @internal */
export const isMultiTouch = (e: any) => e.touches && e.touches.length === 2;

/** @internal */
export const attachEvents = (node: any, handlers: EventHandlers) => {
  Object.keys(handlers).forEach((el) => {
    node.addEventListener(el, handlers[el], { passive: false });
  });
};

/** @internal */
export const detachEvents = (node: any, handlers: EventHandlers) => {
  Object.keys(handlers).forEach((el) => {
    node.removeEventListener(el, handlers[el]);
  });
};

/** @internal */
export const getRect = (
  rotated: boolean,
  interactionWithArguments: Interaction,
  interactionWithValues: Interaction,
  initial: Location,
  current: Location,
  pane: Size,
) => {
  const isZoomArgument = checkInteraction(interactionWithArguments, 'zoom');
  const isZoomValue = checkInteraction(interactionWithValues, 'zoom');
  const isXFixed = rotated ? isZoomValue : isZoomArgument;
  const isYFixed = rotated ? isZoomArgument : isZoomValue;
  const x = isXFixed ? Math.min(initial[0], current[0]) : 0;
  const width = isXFixed ? Math.abs(initial[0] - current[0]) : pane.width;
  const y = isYFixed ? Math.min(initial[1], current[1]) : 0;
  const height = isYFixed ? Math.abs(initial[1] - current[1]) : pane.height;
  return {
    x, y, width, height,
  };
};

const checkInteraction = (interaction: Interaction, type: Interaction) =>
interaction === 'both' || interaction === type;

/** @internal */
export const setCursorType = (node: any, type?: string) => {
  const defaultType = 'pointer';
  node.style.cursor = type ? type : defaultType;
};
import { DIFFERENCE } from '../../constants';

/** @internal */
export const isReadyToRenderSeries = (
  { pane, ...restLayouts }, { current },
  isPreviousDataEmpty: boolean, axesExist: boolean,
) => {
  if (!pane.width && !pane.height) {
    return false;
  }
  const bbox = { width: current.clientWidth, height: current.clientHeight };
  let width = pane.width;
  let height = pane.height;
  Object.entries(restLayouts).forEach((el) => {
    const orientation = el[0].split('-')[0];
    if (orientation === 'top' || orientation === 'bottom') {
      height += el[1].height;
    } else {
      width += el[1].width;
    }
  });
  return Math.abs(bbox.width - width) < DIFFERENCE &&
    Math.abs(bbox.height - height) < DIFFERENCE
    && isPreviousDataEmptyOrNoAxes(isPreviousDataEmpty, axesExist);
};

const isPreviousDataEmptyOrNoAxes = (
  isPreviousDataEmpty: boolean, axesExist: boolean,
) => !isPreviousDataEmpty || !axesExist;
import { area } from 'd3-shape';
import {
  dArea, dRotateArea, dLine, dRotateLine, dSpline, dRotateSpline,
} from '../plugins/series/computeds';
import {
  SeriesList, TransformedPoint, PointList, TargetList, PointDistance, Location,
  CreateHitTesterFn, MakePathFn, IsPointInPathFn,
  HitTestPointFn, Filter,
  BarSeries, ScatterSeries, PieSeries,
  PathFn,
} from '../types';

const getSegmentLength = (dx: number, dy: number) => Math.sqrt(dx * dx + dy * dy);

// *distance* is a normalized distance to point.
// It belongs to [0, Infinity):
//  = 0 - at point center
//  = 1 - at point border
//  > 1 - outside point

// This function is called from event handlers (when DOM is available) -
// *window.document* can be accessed safely.
const createContext = () => document.createElement('canvas').getContext('2d')!;

// For a start using browser canvas will suffice.
// However a better and more clean solution should be found.
// Can't d3 perform hit testing?
const createCanvasAbusingHitTester = (
  makePath: MakePathFn, points: PointList, rotated: boolean,
): IsPointInPathFn => {
  const ctx = createContext();
  const path = makePath(rotated);
  path.context(ctx);
  path(points as any);
  return ([x, y]) => ctx.isPointInPath(x, y);
};

const LINE_POINT_SIZE = 20;
const LINE_TOLERANCE = 10;

const getDistance = (
  [px, py]: Location, { arg, val }: TransformedPoint, rotated: boolean,
) => {
  const x = rotated ? val : arg;
  const y = rotated ? arg : val;
  return getSegmentLength(px - x, py - y);
};

const createContinuousSeriesHitTesterCreator =
  (makePath: MakePathFn): CreateHitTesterFn => (points, rotated) => {
    const fallbackHitTest = createCanvasAbusingHitTester(makePath, points, rotated);
    return (target) => {
      let minDistance = Number.MAX_VALUE;
      let minIndex: number = 0;
      const list: PointDistance[] = [];
      points.forEach((point, i) => {
        const distance = getDistance(target, point as TransformedPoint, rotated);
        if (distance <= LINE_POINT_SIZE) {
          list.push({ distance, index: point.index });
        }
        if (distance < minDistance) {
          minDistance = distance;
          minIndex = i;
        }
      });
      // This is special case for continuous series - if no point is actually hit
      // then the closest point to the pointer position is picked.
      if (!list.length && fallbackHitTest(target)) {
        list.push({ index: points[minIndex].index, distance: minDistance });
      }
      return list.length ? { points: list } : null;
    };
  };

const createPointsEnumeratingHitTesterCreator =
  (hitTestPoint: HitTestPointFn): CreateHitTesterFn => (points, rotated) => (target) => {
    const list: PointDistance[] = [];
    points.forEach((point) => {
      const status = hitTestPoint(target, point as TransformedPoint, rotated);
      if (status) {
        list.push({ index: point.index, distance: status.distance });
      }
    });
    return list.length ? { points: list } : null;
  };

/** @internal */
export const createAreaHitTester = createContinuousSeriesHitTesterCreator((rotated) => {
  const path: PathFn = area() as any;
  const hitArea = rotated ? dRotateArea : dArea;
  if (rotated) {
    path.x1!(hitArea.x1!());
    path.x0!(hitArea.x0!());
    path.y(hitArea.y());
  } else {
    path.x(hitArea.x());
    path.y1!(hitArea.y1!());
    path.y0!(hitArea.y0!());
  }
  return path;
});

/** @internal */
export const createLineHitTester = createContinuousSeriesHitTesterCreator((rotated) => {
  const path: PathFn = area() as any;
  const hitLine = rotated ? dRotateLine : dLine;
  if (rotated) {
    const getX = hitLine.x();
    path.y(hitLine.y());
    path.x0!(point => getX(point) + LINE_TOLERANCE);
    path.x1!(point => getX(point) - LINE_TOLERANCE);
  } else {
    const getY = hitLine.y();
    path.x(hitLine.x());
    path.y1!(point => getY(point) - LINE_TOLERANCE);
    path.y0!(point => getY(point) + LINE_TOLERANCE);
  }
  return path;
});

/** @internal */
export const createSplineHitTester = createContinuousSeriesHitTesterCreator((rotated) => {
  const path: PathFn = area() as any;
  const hitSpline = rotated ? dRotateSpline : dSpline;
  if (rotated) {
    const getX = hitSpline.x();
    path.y(hitSpline.y());
    path.x1!(point => getX(point) - LINE_TOLERANCE);
    path.x0!(point => getX(point) + LINE_TOLERANCE);
  } else {
    const getY = hitSpline.y();
    path.x(hitSpline.x());
    path.y1!(point => getY(point) - LINE_TOLERANCE);
    path.y0!(point => getY(point) + LINE_TOLERANCE);
  }
  path.curve!(hitSpline.curve!());
  return path;
});

const hitTestRect = (dx: number, dy: number, halfX: number, halfY: number) => (
  Math.abs(dx) <= halfX && Math.abs(dy) <= halfY ? {
    distance: getSegmentLength(dx, dy),
  } : null
);

// Some kind of binary search can be used here as bars can be ordered along argument axis.
/** @internal */
export const createBarHitTester = createPointsEnumeratingHitTesterCreator(
  ([px, py], point, rotated) => {
    const {
      arg, val, startVal, barWidth, maxBarWidth,
    } = point as BarSeries.PointProps;
    const halfWidth = maxBarWidth * barWidth / 2;
    const halfHeight = Math.abs(val - startVal!) / 2;
    const centerVal = (val + startVal!) / 2;
    const xCenter = rotated ? centerVal : arg;
    const yCenter = rotated ? arg : centerVal;
    return hitTestRect(
      px - xCenter,
      py - yCenter,
      rotated ? halfHeight : halfWidth,
      rotated ? halfWidth : halfHeight,
    );
  },
);

/** @internal */
export const createScatterHitTester = createPointsEnumeratingHitTesterCreator(
  ([px, py], obj, rotated) => {
    const { point } = obj as ScatterSeries.PointProps;
    const distance = getDistance([px, py], obj, rotated);
    return distance <= point.size / 2 ? { distance } : null;
  },
);

const mapAngleTod3 = (angle: number) => {
  const ret = angle + Math.PI / 2;
  return ret >= 0 ? ret : ret + Math.PI * 2;
};

// Some kind of binary search can be used here as pies can be ordered along angle axis.
/** @internal */
export const createPieHitTester = createPointsEnumeratingHitTesterCreator(
  ([px, py], point) => {
    const {
      arg: x, val: y, innerRadius, outerRadius, startAngle, maxRadius, endAngle,
    } = point as PieSeries.PointProps;
    const inner = innerRadius * maxRadius;
    const outer = outerRadius * maxRadius;
    const rCenter = (inner + outer) / 2;
    const angleCenter = (startAngle + endAngle) / 2;
    const halfRadius = (outer - inner) / 2;
    const halfAngle = Math.abs(startAngle - endAngle) / 2;
    const dx = px - x;
    const dy = py - y;
    const r = getSegmentLength(dx, dy);
    const angle = mapAngleTod3(Math.atan2(dy, dx));
    // This is not a correct distance calculation but for now it will suffice.
    // For Pie series it would not be actually used.
    return hitTestRect(r - rCenter, angle - angleCenter, halfRadius, halfAngle);
  },
);

const buildFilter = (targets: TargetList): Filter => {
  const result = {};
  targets.forEach(({ series, point }) => {
    (result[series] = result[series] || new Set()).add(point);
  });
  return result;
};

/** @internal */
export const changeSeriesState = (seriesList: SeriesList, targets: TargetList, state: string) => {
  if (targets.length === 0) {
    return seriesList;
  }
  const filter = buildFilter(targets);
  let matches = 0;
  const result = seriesList.map((seriesItem) => {
    const set = filter[seriesItem.name];
    if (!set) {
      return seriesItem;
    }
    matches += 1;
    const props: { state: string, points?: TransformedPoint[] } = { state };
    if (set.size) {
      props.points = (seriesItem.points as TransformedPoint[]).map(
        point => (set.has(point.index) ? { ...point, state } : point),
      );
    }
    return { ...seriesItem, ...props };
  });
  // This is to prevent false rerenders.
  return matches > 0 ? result : seriesList;
};
import { SeriesList, LegendItemList } from '../types';

const getDefaultLegendItems = (series: SeriesList): LegendItemList => series.map(
  ({ name: text, color }) => ({ text, color }),
);

const getPieLegendItems = (series: SeriesList): LegendItemList => series[0]
  .points.map(({ argument: text, color }) => ({ text, color }));

// The function supports special case when there is single Pie series.
// There is no common way to tell if series is PieSeries -
// checking `radius` props will suffice for now.
const isSinglePieSeriesCase = (series: SeriesList) => (
  series.length === 1 && 'innerRadius' in series[0] && 'outerRadius' in series[0]
);

/** @internal */
export const getLegendItems = (series: SeriesList) => (
  (isSinglePieSeriesCase(series) ? getPieLegendItems : getDefaultLegendItems)(series)
);
import { hasWindow } from '@devexpress/dx-core';
import { getEventCoords, getOffset } from './common';
import {
  TrackerTarget, HandlerFnList, SeriesList, HitTesters,
  EventHandlerFn, TargetData, EventHandlers, HandlersObject,
} from '../types';

const DISTANCE_THRESHOLD = 20;

const compareHitTargets = (t1: TrackerTarget, t2: TrackerTarget) => {
  const distanceDelta = t1.distance - t2.distance;
  if (Math.abs(distanceDelta) <= DISTANCE_THRESHOLD) {
    const orderDelta = t2.order - t1.order;
    return orderDelta !== 0 ? orderDelta : distanceDelta;
  }
  return distanceDelta;
};

const buildEventHandler = (seriesList: SeriesList, handlers: HandlerFnList): EventHandlerFn => {
  let hitTesters: HitTesters | null = null;

  const createHitTesters = () => {
    const obj: HitTesters = {};
    seriesList.forEach((seriesItem) => {
      obj[seriesItem.symbolName as unknown as string] = seriesItem
      .createHitTester(seriesItem.points, seriesItem.rotated);
    });
    return obj;
  };

  return (e) => {
    const location = getEventCoords(e, getOffset(e.currentTarget));
    hitTesters = hitTesters || createHitTesters();
    const targets: TrackerTarget[] = [];
    seriesList.forEach(({ name: series, index: order, symbolName }) => {
      const status = hitTesters![symbolName as unknown as string](location);
      if (status) {
        targets.push(...status.points.map(
          point => ({
            series, order, point: point.index, distance: point.distance,
          }),
        ));
      }
    });
    targets.sort(compareHitTargets);
    const arg: TargetData = { location, targets, event: e.nativeEvent };
    handlers.forEach(handler => handler(arg));
  };
};

const buildLeaveEventHandler = (handlers: HandlerFnList): EventHandlerFn => (e) => {
  const location = getEventCoords(e, getOffset(e.currentTarget));
  const arg: TargetData = { location, targets: [] };
  handlers.forEach(handler => handler(arg));
};

// The result is of Map<string, Function> type.
// Keys are DOM event names (https://developer.mozilla.org/en-US/docs/Web/Events).
/** @internal */
export const buildEventHandlers = (
  seriesList: SeriesList, { clickHandlers, pointerMoveHandlers }: HandlersObject,
) => {
  const handlers: EventHandlers = {};
  if (!hasWindow()) return handlers;
  if (clickHandlers.length) {
    handlers.click = buildEventHandler(seriesList, clickHandlers);
  }
  if (pointerMoveHandlers.length) {
    const moveHandler = buildEventHandler(seriesList, pointerMoveHandlers);
    const leaveHandler = buildLeaveEventHandler(pointerMoveHandlers);
    if ('ontouchstart' in window) {
      handlers.touchstart = moveHandler;
    } else {
      handlers.mousemove = moveHandler;
      handlers.mouseleave = leaveHandler;
    }
  }
  return handlers;
};
import {
  EasingFn, SetAttributeFn, AnimationFn, GetNewPositionsFn,
  RangePointCoordinates, PointCoordinates, PointComponentProps,
  PieCoordinates, PathStartCoordinates, PathEndCoordinates,
} from '../types';

/** @internal */
const getProgress = ({ elapsed, total }: {elapsed: number, total: number}) =>
Math.min(elapsed / total, 1);

/** @internal */
const runAnimation = (
  setAttributes: SetAttributeFn,
  getNewPositions: GetNewPositionsFn,
  easing: EasingFn, duration: number, delay: number,
) => new Promise((resolve) => {
  setTimeout(() => {
    const time = {
      start: Date.now(),
      total: duration,
      elapsed: 0,
    };
    const step = () => {
      time.elapsed = Date.now() - time.start;
      const progress = getProgress(time);
      setAttributes(getNewPositions(easing(progress)));

      if (progress < 1) requestAnimationFrame(step);
    };
    resolve(requestAnimationFrame(step));
  }, delay);
});

/** @internal */
export const buildAnimation = (easing: EasingFn, duration: number): AnimationFn => (
  startCoords, endCoords, processAnimation, setAttributes, delay = 0,
) => {
  let animationID;

  const stop = () => {
    if (animationID) {
      cancelAnimationFrame(animationID);
      animationID = undefined;
    }
  };

  const run = (start, end, delayValue) => {
    animationID = runAnimation(
      setAttributes, processAnimation(start, end),
      easing, duration, delayValue,
    ).then((res) => {
      animationID = res;
    });
  };

  run(startCoords, endCoords, delay);

  return {
    update: (updatedStartCoords, updatedEndCoords, updatedDelay = 0) => {
      stop();
      run(updatedStartCoords, updatedEndCoords, updatedDelay);
    },
    stop,
  };
};

const lerp = (a: number, b: number, t: number) => a + t * (b - a);

/** @internal */
export const processPointAnimation = (
  startCoords: PointCoordinates, endCoords: PointComponentProps,
) => {
  return (progress: number) => {
    return {
      arg: lerp(startCoords.arg, endCoords.arg, progress),
      val: lerp(startCoords.val, endCoords.val, progress),
    };
  };
};

/** @internal */
export const processBarAnimation = (
  startCoords: RangePointCoordinates, endCoords: PointComponentProps,
) => {
  return (progress: number) => {
    return {
      arg: lerp(startCoords.arg, endCoords.arg, progress),
      val: lerp(startCoords.val, endCoords.val, progress),
      startVal: lerp(startCoords.startVal, endCoords.startVal, progress),
    };
  };
};

/** @internal */
export const processLineAnimation = (
  { coordinates }: PathStartCoordinates,
  { coordinates: endCoordinates }: PathEndCoordinates,
) => {
  return (progress: number) => {
    return {
      coordinates: endCoordinates.map((coord, index) => {
        const startCurCoord = coordinates[index];
        return {
          ...coord,
          arg: lerp(startCurCoord.arg, coord.arg, progress),
          val: lerp(startCurCoord.val, coord.val, progress),
        };
      }),
    };
  };
};

/** @internal */
export const processAreaAnimation = (
  { coordinates }: PathStartCoordinates,
  { coordinates: endCoordinates }: PathEndCoordinates,
) => {
  return (progress: number) => {
    return {
      coordinates: endCoordinates.map((coord, index) => {
        const startCurCoord = coordinates[index];
        return {
          ...coord,
          arg: lerp(startCurCoord.arg, coord.arg, progress),
          val: lerp(startCurCoord.val, coord.val, progress),
          startVal: lerp(startCurCoord.startVal, coord.startVal, progress),
        };
      }),
    };
  };
};

/** @internal */
export const processPieAnimation = (start: PieCoordinates, end: PieCoordinates) => {
  return (progress: number) => {
    return {
      innerRadius: lerp(start.innerRadius, end.innerRadius, progress),
      outerRadius: lerp(start.outerRadius, end.outerRadius, progress),
      startAngle: lerp(start.startAngle, end.startAngle, progress),
      endAngle: lerp(start.endAngle, end.endAngle, progress),
    };
  };
};
