import React from "react";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import dayjs from "dayjs";

const COLORS = ["#8884d8", "#82ca9d", "#ffc658", "#ff7f50", "#6a5acd", "#ff69b4", "#32cd32"];

export default function TransactionChart({ data }) {
  const chartData = data.map((item, index) => ({
    name: dayjs().month(item._id - 1).format("MMMM"),
    value: item.totalExpenses,
    color: COLORS[index % COLORS.length], // Assign colors dynamically
  }));

  return (
    <Paper sx={{ padding: 3, textAlign: "center", elevation: 3 }}>
      <Typography variant="h6" sx={{ marginBottom: 2 }}>
        Monthly Expenses
      </Typography>
      <PieChart width={400} height={300}>
        <Pie
          data={chartData}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={100}
          innerRadius={50} // Creates a donut shape
          label
        >
          {chartData.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={entry.color} />
          ))}
        </Pie>
        <Tooltip />
        <Legend />
      </PieChart>
    </Paper>
  );
}
